mapper.select:
select 

from del_outbound_tarck_on
where
    1=1

  AND (order_no IN

    #{orderNo}

  or tracking_no IN

    #{trackingNo}

  or tracking_no_new IN

    #{trackingNos}

      )



  AND id IN

    #{id}




  and date_format(update_time,'%y%m%d') >= date_format(#{startTime},'%y%m%d')


  and date_format(update_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')

mapper.update:
update del_outbound_tarck_on
set order_no = #{orderNo,jdbcType=VARCHAR},
  tracking_no = #{trackingNo,jdbcType=VARCHAR},
  tracking_no_new = #{trackingNoNew,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
