mapper.select:
select FASB.nature as business_category,FASB.`no`,FASB.currency_code,FASB.payment_time, FASB.cus_code,
'国内直发' as order_type,
'仓储服务' as order_type,
'大货服务' as order_type,
DOB.tracking_no,DOB.ref_no,DOB.warehouse_code, DOB.shipment_rule,DOB.shipment_service, DOB.shipment_rule_name, DOB.destination_delivery,DOB.weight,DOB.forecast_weight, DOB.calc_weight,DOB.calc_weight_unit, DOB.create_time, DOB.specifications,DOB.amazon_logistics_route_id, DOD.product_name,DOD.sku, DOA.country,DOA.country_code, sum(FASB.amount) as total_amount, sum(case when FASB.charge_category = '物流基础费' then abs(FASB.amount) else 0 end ) as freight_fee, sum(case when FASB.charge_category in ('操作费','业务操作') then abs(FASB.amount) else 0 end) as ex_warehourse_fee, sum(case when FASB.charge_category = '物料费' then abs(FASB.amount) else 0 end) as package_fee, sum(case when FASB.charge_category = '处理费' then abs(FASB.amount) else 0 end) as hander_fee, sum(case when FASB.charge_category = '燃油附加费' then abs(FASB.amount) else 0 end) as fuel_fee, sum(case when FASB.charge_category = '偏远地区费' then abs(FASB.amount) else 0 end) as remote_fee, sum(case when FASB.charge_category in ('偏远地区费','住宅派送费','增值税','挂号费','超大附加费','超重处理费','退费') then FASB.amount else 0 end) as other_fee from fss_account_serial_bill as FASB inner join del_outbound as DOB on FASB.`no` = DOB.order_no left join del_outbound_detail as DOD on DOB.order_no = DOD.order_no left join del_outbound_address as DOA on DOB.order_no = DOA.order_no
and DOB.del_flag = 0
and DOB.order_type in
#{item}
and FASB.cus_code = #{cusCode}
and FASB.payment_time >= #{billStartTime}
and FASB.payment_time <= #{billEndTime}
group by FASB.nature,FASB.no,FASB.currency_code,FASB.payment_time

SELECT a.id,a.no,a.tracking_no,a.cus_code,a.cus_name,a.currency_code,a.currency_name,a.amount, a.warehouse_code,a.warehouse_name,a.pay_method,a.business_category,a.product_code,a.product_category, a.charge_category,a.charge_type,a.create_by_name,a.create_time,a.update_by_name,a.update_time, a.create_by,a.update_by,a.remark,a.order_time,a.payment_time,a.ref_no,a.shipment_service,a.weight, a.calc_weight,a.specifications,a.serial_number,a.amazon_logistics_route_id,a.country_code,a.country FROM `fss_account_serial_bill` a
and a.cus_code = #{cusCode}
and (a.business_category in ('线下充值','在线充值') or a.business_category like '%转%' or a.business_category like '%提现%')
and a.business_category in ('优惠','赔偿')
and a.payment_time >= #{billStartTime}
and a.payment_time <= #{billEndTime}

select FASB.charge_category as chargeCategory,FASB.currency_code as currencyCode,FASB.warehouse_code as warehouse, '物流消费' as nature,
'国内直发' as businessType,
'仓储服务' as businessType,
'大货服务' as businessType,
sum(FASB.amount) as amount,FASB.remark from fss_account_serial_bill as FASB inner join del_outbound as DOB on FASB.`no` = DOB.order_no
and DOB.del_flag = 0 and FASB.business_category not in ('优惠','退费','赔偿','补收')
and DOB.order_type in
#{item}
and FASB.cus_code = #{cusCode}
and FASB.payment_time >= #{billStartTime}
and FASB.payment_time <= #{billEndTime}
group by FASB.charge_category,FASB.currency_code,FASB.warehouse_code

select
    FASB.charge_category as chargeCategory,FASB.currency_code as currencyCode,
    '充值' as nature,
    sum(FASB.amount) as amount,FASB.remark
from fss_account_serial_bill as FASB


    and FASB.charge_category like '%充值%'


        and FASB.cus_code = #{cusCode}



        and FASB.payment_time  >=  #{billStartTime}



        and FASB.payment_time  <=  #{billEndTime}



group by FASB.charge_category,FASB.currency_code

select
FASB.charge_category as chargeCategory,FASB.currency_code as currencyCode,
'提现' as nature,
sum(FASB.amount) as amount,FASB.remark
from fss_account_serial_bill as FASB


    and FASB.business_category like '%提现%'


        and FASB.cus_code = #{cusCode}



        and FASB.payment_time  >=  #{billStartTime}



        and FASB.payment_time  <=  #{billEndTime}



group by FASB.charge_category,FASB.currency_code

select
FASB.charge_category as chargeCategory,FASB.warehouse_code as warehouse,FASB.currency_code as currencyCode,
'优惠/退费/赔偿' as nature,
sum(FASB.amount) as amount,FASB.remark
from fss_account_serial_bill as FASB


    and FASB.business_category in ('优惠','退费','赔偿')


        and FASB.cus_code = #{cusCode}



        and FASB.payment_time  >=  #{billStartTime}



        and FASB.payment_time  <=  #{billEndTime}



group by FASB.charge_category,FASB.warehouse_code,FASB.currency_code

select
FASB.charge_category as chargeCategory,FASB.currency_code as currencyCode,
'补收' as nature,
sum(FASB.amount) as amount,FASB.remark
from fss_account_serial_bill as FASB


    and FASB.business_category like '%补收%'


        and FASB.cus_code = #{cusCode}



        and FASB.payment_time  >=  #{billStartTime}



        and FASB.payment_time  <=  #{billEndTime}



group by FASB.charge_category,FASB.currency_code

