mapper.select:
SELECT

FROM inbound_receipt_detail t


        AND t.id = #{id}


        AND t.warehouse_no = #{warehouseNo}


        AND t.sku = #{sku}

SELECT sku, sku_name, SUM( declare_qty ) AS forecastTotal, SUM( put_qty ) actualOnShelvesTotal, SUM( inTransitTotal ) inTransitTotal FROM ( SELECT sku, sku_name, IF (`status` = 5 OR `status` = 0 , 0 , declare_qty) as declare_qty, put_qty, `status`, IF ( `status` = 5 OR `status` = 0, 0, IF (( declare_qty - put_qty ) > 0,( declare_qty - put_qty ), 0)) AS inTransitTotal FROM `inbound_receipt_detail` ird LEFT JOIN inbound_receipt ir ON ir.warehouse_no = ird.warehouse_no
AND ird.create_time >= #{cm.timeStart} AND ird.create_time <= #{cm.timeEnd}
AND ird.sku IN
#{item}
AND ir.cus_code = #{sellerCode}
AND status in (0,3,4,5)
) temp GROUP BY sku

SELECT COUNT(1) FROM inbound_receipt ir RIGHT JOIN (
        SELECT warehouse_no FROM inbound_receipt_detail WHERE delivery_no = #{deliveryNo}
        ) temp ON temp.warehouse_no = ir.warehouse_no  WHERE ir.`status`  != 0
